--
-- Structure de la table `wp_posts`
--

CREATE TABLE `wp_posts` (
  `ID` bigint(20) UNSIGNED NOT NULL,
  `post_author` bigint(20) UNSIGNED NOT NULL DEFAULT '0',
  `post_date` datetime NOT NULL DEFAULT '0000-00-00 00:00:00',
  `post_date_gmt` datetime NOT NULL DEFAULT '0000-00-00 00:00:00',
  `post_content` longtext COLLATE utf8mb4_unicode_520_ci NOT NULL,
  `post_title` text COLLATE utf8mb4_unicode_520_ci NOT NULL,
  `post_excerpt` text COLLATE utf8mb4_unicode_520_ci NOT NULL,
  `post_status` varchar(20) COLLATE utf8mb4_unicode_520_ci NOT NULL DEFAULT 'publish',
  `comment_status` varchar(20) COLLATE utf8mb4_unicode_520_ci NOT NULL DEFAULT 'open',
  `ping_status` varchar(20) COLLATE utf8mb4_unicode_520_ci NOT NULL DEFAULT 'open',
  `post_password` varchar(255) COLLATE utf8mb4_unicode_520_ci NOT NULL DEFAULT '',
  `post_name` varchar(200) COLLATE utf8mb4_unicode_520_ci NOT NULL DEFAULT '',
  `to_ping` text COLLATE utf8mb4_unicode_520_ci NOT NULL,
  `pinged` text COLLATE utf8mb4_unicode_520_ci NOT NULL,
  `post_modified` datetime NOT NULL DEFAULT '0000-00-00 00:00:00',
  `post_modified_gmt` datetime NOT NULL DEFAULT '0000-00-00 00:00:00',
  `post_content_filtered` longtext COLLATE utf8mb4_unicode_520_ci NOT NULL,
  `post_parent` bigint(20) UNSIGNED NOT NULL DEFAULT '0',
  `guid` varchar(255) COLLATE utf8mb4_unicode_520_ci NOT NULL DEFAULT '',
  `menu_order` int(11) NOT NULL DEFAULT '0',
  `post_type` varchar(20) COLLATE utf8mb4_unicode_520_ci NOT NULL DEFAULT 'post',
  `post_mime_type` varchar(100) COLLATE utf8mb4_unicode_520_ci NOT NULL DEFAULT '',
  `comment_count` bigint(20) NOT NULL DEFAULT '0'
) ENGINE=InnoDB DEFAULT CHARSET=utf8mb4 COLLATE=utf8mb4_unicode_520_ci;

--
-- Contenu de la table `wp_posts`
--

INSERT INTO `wp_posts` (`ID`, `post_author`, `post_date`, `post_date_gmt`, `post_content`, `post_title`, `post_excerpt`, `post_status`, `comment_status`, `ping_status`, `post_password`, `post_name`, `to_ping`, `pinged`, `post_modified`, `post_modified_gmt`, `post_content_filtered`, `post_parent`, `guid`, `menu_order`, `post_type`, `post_mime_type`, `comment_count`) VALUES
(1, 1, '2019-04-11 10:43:12', '2019-04-11 08:43:12', '<!-- wp:paragraph -->\n<p>Bienvenue sur WordPress. Ceci est votre premier article. Modifiez-le ou supprimez-le, puis commencez √† √©crire¬†!</p>\n<!-- /wp:paragraph -->', 'Bonjour tout le monde¬†!', '', 'publish', 'open', 'open', '', 'bonjour-tout-le-monde', '', '', '2019-04-11 10:43:12', '2019-04-11 08:43:12', '', 0, 'http://app.local/?p=1', 0, 'post', '', 1),
(2, 1, '2019-04-11 10:43:12', '2019-04-11 08:43:12', '<!-- wp:paragraph -->\n<p>Ceci est une page d‚Äôexemple. C‚Äôest diff√©rent d‚Äôun article de blog parce qu‚Äôelle restera au m√™me endroit et appara√Ætra dans la navigation de votre site (dans la plupart des th√®mes). La plupart des gens commencent par une page ¬´¬†√Ä propos¬†¬ª qui les pr√©sente aux visiteurs du site. Cela pourrait ressembler √† quelque chose comme cela¬†:</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:quote -->\n<blockquote class=\"wp-block-quote\"><p>Bonjour¬†! Je suis un m√©canicien qui aspire √† devenir acteur, et voici mon site. J‚Äôhabite √† Bordeaux, j‚Äôai un super chien baptis√© Russell, et j‚Äôaime la vodka-ananas (ainsi qu‚Äô√™tre surpris par la pluie soudaine lors de longues balades sur la plage au coucher du soleil).</p></blockquote>\n<!-- /wp:quote -->\n\n<!-- wp:paragraph -->\n<p>... ou quelque chose comme cela¬†:</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:quote -->\n<blockquote class=\"wp-block-quote\"><p>La soci√©t√© 123 Machin Truc a √©t√© cr√©√©e en 1971, et n‚Äôa cess√© de proposer au public des machins-trucs de qualit√© depuis lors. Situ√©e √† Saint-Remy-en-Bouzemont-Saint-Genest-et-Isson, 123 Machin Truc emploie 2 000 personnes, et fabrique toutes sortes de bidules super pour la communaut√© bouzemontoise.</p></blockquote>\n<!-- /wp:quote -->\n\n<!-- wp:paragraph -->\n<p>En tant que nouvel¬∑le utilisateur ou utilisatrice de WordPress, vous devriez vous rendre sur <a href=\"http://app.local/wp-admin/\">votre tableau de bord</a> pour supprimer cette page et cr√©er de nouvelles pages pour votre contenu. Amusez-vous bien¬†!</p>\n<!-- /wp:paragraph -->', 'Page d‚Äôexemple', '', 'trash', 'closed', 'open', '', 'page-d-exemple__trashed', '', '', '2019-04-11 16:05:24', '2019-04-11 14:05:24', '', 0, 'http://app.local/?page_id=2', 0, 'page', '', 0),
